import React, { useEffect, useState } from "react";

function Input (){
    
    var text = ''
    const onch = (event)=> {
        text = event.target.value
         
    }
    useEffect(()=>{
        document.title = text
        console.log(text)
    },[text])

    return(
        <div>
            <br></br>
            <from>
                <input onChange={onch} type="text"></input>
            </from>

            <ul>
                <li>{text}</li>
            </ul>
        </div>
        
        
    )

}


export default Input;




