import { useState, useEffect } from "react";


function Timer() {
  const [count, setCount] = useState(0);

  useEffect(() => {
    const idIntervalo = setTimeout(() => {
      setCount(count + 1);
    }, 1000);
    if (count === 5) {
        console.log('Pronto');
        clearInterval(idIntervalo);
      }
  }, [count]);

  return <h1>useEffect {count} segusdos!</h1>;
}

export default Timer;
